package com.gfgTcs.String;

import java.util.Scanner;

public class IndexofSubstring {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		String s=sc.next();
		String r=sc.next();
		int ind=s.indexOf(r);
		System.out.println(ind);
		

	}

}
